export default function intervalPrint(str) {
  setInterval(printByChar(str), 1000);
}
*/


export default function printByChar(str) {

  let charArray = [...str];
  for(let i = 0; i < charArray.length; i++) {
    ReactDOM.render(charArray[i])
    return charArray[i];
  }
}
