import React from 'react';
import Avatar from './Avatar.jsx';
import Profile from './Profile.jsx';
import Links from './Links.jsx';
import Description from './Description.jsx';

const socialMedia = [
  {
    name: 'twitter',
    href: 'https://twitter.com/HAL9000_',
    img: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/210284/social-twitter.svg',
    alt: 'twitter link'
  },
  {
    name: 'fandom',
    href: 'https://2001.fandom.com/wiki/HAL_9000',
    img: 'https://avatars2.githubusercontent.com/u/1171011?s=200&v=4',
    alt: 'fandom link'
  },
  {
    name: 'wikipedia',
    href: 'https://en.wikipedia.org/wiki/HAL_9000',
    img: 'https://upload.wikimedia.org/wikipedia/en/thumb/8/80/Wikipedia-logo-v2.svg/1200px-Wikipedia-logo-v2.svg.png',
    alt: 'wikipedia link'
  }
]

class Card extends React.Component {

  constructor(props) {
    super(props);
    this.object = props.object;
  }

  render() {
    return (
      <aside className='profile-card'>
        <header>
          <Avatar 
            className={this.object.className}
            src={this.object.src}
            alt={this.object.alt}
          />
          <Description
            name={this.object.name}
            description={this.object.description}
          />
        </header>
        <Profile
          profile={this.object.text}
        />
        <Links
          socialMedia={this.object}
        />
      </aside>
    );
  }
}

export default Card;
