import React from 'react';
import Avatar from './Avatar.jsx';
import Profile from './Profile.jsx';

const profile = {
  text: 'The HAL (Heuristically programmed ALgorithmic Computer) 9000 computer is an artificial intelligence and the onboard computer on the spaceship Discovery 1. HAL 9000, more commonly called "Hal", became operational in the HAL plant in Urbana, Illinois, on January 12, 1992. His first instructor was Dr. Chandra.  He is the hidden main antagonist of 2001: A Space Odyssey and a major protagonist in 2010.',
}


class Character extends React.Component {

  constructor(props) {
    super(props);
    this.name = props.name;
    this.description = props.description;
  }

  render() {
    return (
      <aside className='profile-card'>
        <header>
          <Avatar />
          <h1>{this.name}</h1>
          <h2>{this.description}</h2>
        </header>
        <Profile
          profile={profile.text}
        />
      </aside>
    );
  }
}

export default Character;
