/*
export default function intervalPrint(str) {
  setInterval(printByChar(str), 1000);
}
*/
const hal9000 = {
  className: 'Hal9000',
  src: 'https://www.jcheudin.fr/playground/playground/hal9000/images/haleye.gif',
  alt: 'Hal9000',
  name: 'Hal 9000',
  description: '"Good afternoon, gentlemen. I am a HAL 9000 computer."',
  text: 'The HAL (Heuristically programmed ALgorithmic Computer) 9000 computer is an artificial intelligence and the onboard computer on the spaceship Discovery 1. HAL 9000, more commonly called "Hal", became operational in the HAL plant in Urbana, Illinois, on January 12, 1992. His first instructor was Dr. Chandra.  He is the hidden main antagonist of 2001: A Space Odyssey (1968) and a major protagonist in 2010: Odyssey Two (1982).\n Hal is capable of many functions, such as speech, speech recognition, facial recognition, lip reading, interpreting emotions, expressing emotions, and chess, in addition to maintaining all systems on Discovery.',
}


export default function printByChar(str) {

  let charArray = [...str];
  for(let i = 0; i < charArray.length; i++) {
    ReactDOM.render(charArray[i], document.getElementById('root'));
  }
}
